import { RouterProvider, createBrowserRouter } from "react-router-dom";

import EditEventPage from "./pages/EditEvent";
import HomePage from "./pages/Home";
import ErrorPage from "./pages/Error";
import NewEventPage from "./pages/NewEvent";
import EventsPage, { loader as eventsLoader } from "./pages/Events";
import EventDetailPage, {
  loader as eventDetailLoader,
} from "./pages/EventDetail";
import RootLayout from "./pages/Root";
import EventsRootLayout from "./pages/EventsRoot";

// why wouldn't the path - '/events/new' trigger the path definition for '/events/:id'?
// React is smart and understands /events/new is specific and hence prefers its definition any time the path is accessed (rather than the route definition of /events/:id)

const router = createBrowserRouter([
  {
    path: "/",
    errorElement: <ErrorPage></ErrorPage>,
    element: <RootLayout></RootLayout>,
    // reletive paths
    children: [
      { index: true, element: <HomePage /> },
      {
        path: "events",
        element: <EventsRootLayout></EventsRootLayout>,
        children: [
          {
            index: true,
            element: <EventsPage></EventsPage>,
            loader: eventsLoader,
          },
          {
            path: ':eventId',
            loader: eventDetailLoader,
            children: [
              {
                index: true,
                element: <EventDetailPage></EventDetailPage>,
              },
              { path: "edit", element: <EditEventPage></EditEventPage> },
            ]
          },
          { path: "new", element: <NewEventPage></NewEventPage> },

        ],
      },
    ],
  },
]);

function App() {
  return <RouterProvider router={router} />;
}

export default App;
